import React from 'react';
import './Turismo.css'; // Para estilizar os cartões
import aparecidaImg from '../assets/aparecida.jpg';
import betoCarreroImg from '../assets/beto-carrero.jpg';
import { Link } from 'react-router-dom';

const Turismo = () => {
  const destinos = [
    {
      nome: 'Beto Carrero World',
      img: betoCarreroImg,
      ida: '26/11 as 22:00',
      retorno: '27/11 as 20:00',
    },
    {
      nome: 'Aparecida do Norte',
      img: aparecidaImg,
      ida: '26/11 as 22:00',
      retorno: '27/11 as 20:00',
    },
  ];

  return (
    <div className="turismo-page">
      <h2 className="turismo-title">Nossos Destinos</h2>
      <div className="destinos-container">
        {destinos.map((destino, index) => (
          <div className="destino-card" key={index}>
            <img src={destino.img} alt={destino.nome} className="destino-img" />
            <div className="destino-info">
              <h3>{destino.nome}</h3>
              <p>Ida: {destino.ida}</p>
              <p>Retorno: {destino.retorno}</p>
              <Link to="/contato" className="destino-button">Entre em Contato</Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Turismo;
