import React from 'react';
import { Link } from 'react-router-dom';
import './Inicio.css';

function Inicio() {
  return (
    <div className="inicio-page">
      
      {/* Espaço para o carrossel */}
      <div className="carousel-placeholder">
        Carrossel (substitua por um carrossel real)
      </div>
      
      {/* Container para centralizar as seções */}
      <div className="sections-container">
        
        {/* Seção de Frete de Ônibus */}
        <div className="section-box">
          <Link to="/alugueldeonibus" className="section-link">
            <div className="section-inner">Frete de Ônibus</div>
          </Link>
        </div>

        {/* Seção de Nossa Frota */}
        <div className="section-box">
          <Link to="/nossafrota" className="section-link">
            <div className="section-inner">Nossa Frota</div>
          </Link>
        </div>

        {/* Seção de Turismo */}
        <div className="section-box">
          <Link to="/turismo" className="section-link">
            <div className="section-inner">Turismo</div>
          </Link>
        </div>
      </div>

      {/* Footer fixo com direitos autorais e redes sociais */}
      <footer className="footer">
        <span className="footer-text">© 2024 Aloha Turismo - Todos os direitos reservados</span>
        <div className="social-links">
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">
            <img src="link-para-icone-facebook.png" alt="Facebook" />
          </a>
          <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">
            <img src="link-para-icone-instagram.png" alt="Instagram" />
          </a>
          <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
            <img src="link-para-icone-twitter.png" alt="Twitter" />
          </a>
        </div>
      </footer>
    </div>
  );
}

export default Inicio;
