import React from 'react';
import { Link } from 'react-router-dom';
import { FaWhatsapp, FaInstagram, FaFacebook, FaEnvelope } from 'react-icons/fa';
import SimpleMap from '../components/SimpleMap';
import './Contato.css';

const Contato = () => {
  return (
    <div className="contact-page">
      <h2 style={{ textAlign: 'center', marginTop: '60px', fontSize: '24px' }}>
        Entre em contato conosco
      </h2>
      
      {/* Informações de contato */}
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
          <Link to="https://wa.me/99999999999" target="_blank" style={{ display: 'flex', alignItems: 'center', textDecoration: 'none' }}>
            <FaWhatsapp size={30} color="#25D366" style={{ marginRight: '10px' }} className="contact-icon" />
            <p style={{ fontSize: '18px', margin: '0' }}>(99) 99999-9999</p>
          </Link>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
          <Link to="https://www.instagram.com/aloha_turismo" target="_blank" style={{ display: 'flex', alignItems: 'center', textDecoration: 'none' }}>
            <FaInstagram size={30} color="#E1306C" style={{ marginRight: '10px' }} className="contact-icon" />
            <p style={{ fontSize: '18px', margin: '0' }}>@aloha_turismo</p>
          </Link>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
          <Link to="https://www.facebook.com/AlohaTurismo" target="_blank" style={{ display: 'flex', alignItems: 'center', textDecoration: 'none' }}>
            <FaFacebook size={30} color="#3b5998" style={{ marginRight: '10px' }} className="contact-icon" />
            <p style={{ fontSize: '18px', margin: '0' }}>/AlohaTurismo</p>
          </Link>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <FaEnvelope size={30} color="#000" style={{ marginRight: '10px' }} className="contact-icon" />
            <p style={{ fontSize: '18px', margin: '0' }}>contato@alohaturismo.com</p>
          </div>
        </div>
      </div>

      {/* Adicionando o mapa */}
      <SimpleMap />
    </div>
  );
};

export default Contato;
