import React from 'react';
import './Turismo.css'; // Arquivo de estilo para estilização da página
import { Link } from 'react-router-dom';

const destinos = [
  {
    id: 1,
    nome: 'Beto Carrero World',
    imagem: '../assets/aparecida.jpg', // Atualize com o caminho correto
    ida: '26/11 às 22:00',
    retorno: '27/11 às 20:00',
  },
  {
    id: 2,
    nome: 'Aparecida do Norte',
    imagem: '..assets/be', // Atualize com o caminho correto
    ida: '26/11 às 22:00',
    retorno: '27/11 às 20:00',
  },
];

const Turismo = () => {
  return (
    <div className="turismo-page">
      <h2 className="turismo-title">Nossos Destinos</h2>
      <div className="destinos-container">
        {destinos.map((destino) => (
          <div className="destino-card" key={destino.id}>
            <img src={destino.imagem} alt={destino.nome} className="destino-imagem" />
            <div className="destino-info">
              <h3 className="destino-nome">{destino.nome}</h3>
              <p className="destino-ida">Ida: {destino.ida}</p>
              <p className="destino-retorno">Retorno: {destino.retorno}</p>
              <Link to="/contato" className="contato-botao">Entre em Contato</Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Turismo;
