import React, { useEffect, useRef } from 'react';
import mapboxgl from 'mapbox-gl'; // Importa a biblioteca do Mapbox

const SimpleMap = () => {
  const mapRef = useRef(null);
  const coordinates = { lat: -25.359376907348633, lng: -51.476539611816406 };
  
  // Substitua 'YOUR_MAPBOX_ACCESS_TOKEN' pelo seu token do Mapbox
  mapboxgl.accessToken = 'YOUR_MAPBOX_ACCESS_TOKEN';

  useEffect(() => {
    const map = new mapboxgl.Map({
      container: mapRef.current,
      style: 'mapbox://styles/mapbox/streets-v11', // Escolha o estilo do mapa
      center: [coordinates.lng, coordinates.lat], // [lng, lat]
      zoom: 14
    });

    new mapboxgl.Marker()
      .setLngLat([coordinates.lng, coordinates.lat])
      .setPopup(new mapboxgl.Popup().setHTML('<h3>Estamos aqui</h3>')) // Adiciona um popup
      .addTo(map);
  }, [coordinates]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h3 style={{ textAlign: 'center', fontSize: '20px', margin: '20px 0' }}>Nossa Localização</h3>
      <div
        ref={mapRef}
        style={{ width: '100%', height: '300px', marginTop: '10px', marginBottom: '20px' }} 
      ></div>
    </div>
  );
};

export default SimpleMap;
