import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaBars, FaTimes } from 'react-icons/fa'; // Ícones para abrir e fechar o menu
import './Header.css';

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header className="header">
      <div className="navbar">
        <Link to="/" className="navbar-logo">
          Aloha Turismo
        </Link>
        <div className="menu-icon" onClick={toggleMenu}>
          {menuOpen ? <FaTimes /> : <FaBars />}
        </div>
        <nav className={`navbar-links ${menuOpen ? 'active' : ''}`}>
          <Link to="/" onClick={toggleMenu}>Início</Link>
          <Link to="/aluguel-onibus" onClick={toggleMenu}>Aluguel de Ônibus</Link>
          <Link to="/turismo" onClick={toggleMenu}>Turismo</Link>
          <Link to="/nossa-frota" onClick={toggleMenu}>Nossa Frota</Link>
          <Link to="/contato" onClick={toggleMenu}>Contato</Link>
        </nav>
      </div>
    </header>
  );
};

export default Header;
