import React, { useEffect, useRef } from 'react';

const SimpleMap = () => {
  const mapRef = useRef(null);
  const coordinates = { lat: -25.359376907348633, lng: -51.476539611816406 };

  useEffect(() => {
    const loadMap = () => {
      const { google } = window;

      if (mapRef.current) {
        const map = new google.maps.Map(mapRef.current, {
          center: coordinates,
          zoom: 14,
          scrollwheel: false,
        });

        new google.maps.marker.AdvancedMarkerElement({
          position: coordinates,
          map: map,
          title: 'Estamos aqui',
        });
      }
    };

    if (window.google) {
      loadMap();
    } else {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyAU_LI1KNjTOaRhkeFah0rJuton0r-xekk&callback=loadMap`;
      script.async = true;
      script.defer = true;
      document.body.appendChild(script);
      window.loadMap = loadMap; // Defina loadMap como função global
    }
  }, [coordinates]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h3 style={{ textAlign: 'center', fontSize: '20px', margin: '20px 0' }}>Nossa Localização</h3>
      <div
        ref={mapRef}
        style={{ width: '100%', maxWidth: '600px', height: '300px', marginTop: '10px', marginBottom: '20px', position: 'relative' }}
      ></div>
    </div>
  );
};

export default SimpleMap;
