import React from 'react';
import GoogleMapReact from 'google-map-react';

const Marker = ({ text }) => <div style={{
  color: "white",
  background: "red",
  padding: "10px 15px",
  borderRadius: "50%",
  textAlign: "center",
}}>{text}</div>;

function SimpleMap() {
  const center = { lat: -23.563987, lng: -46.654321 }; // Substitua pelas coordenadas da empresa
  const zoom = 15;

  return (
    <div style={{ height: '400px', width: '100%', marginTop: '2rem' }}>
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'SUA_CHAVE_DE_API' }} // Substitua pela chave de API do Google Maps
        defaultCenter={center}
        defaultZoom={zoom}
      >
        <Marker
          lat={center.lat}
          lng={center.lng}
          text="Aloha Turismo"
        />
      </GoogleMapReact>
    </div>
  );
}

export default SimpleMap;
