import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBus } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import useForm from '../hooks/useForm'; // Importando o hook
import './AluguelOnibus.css';

const AluguelOnibus = () => {
  const navigate = useNavigate();

  // Inicialização do hook useForm
  const { values, handleChange, handleSubmit } = useForm({
    nome: '',
    email: '',
    telefone: '',
    destino: '',
    dataIda: '',
    dataRetorno: ''
  });

  // Função de envio
  const onSubmit = (formData) => {
    console.log('Formulário enviado:', formData);
    // Aqui você pode adicionar lógica para enviar os dados para um servidor
  };

  const cards = [
    // Mesmos dados que antes
    { text: "Desenvolvemos uma logística de transporte otimizada para sua empresa..." },
    { text: "Atendemos os mais diferentes tipos de eventos no Brasil e Exterior..." },
    { text: "Seu sonho é conhecer lugares diferentes?..." },
    { text: "Grupos da melhor idade, religiosos, familiares..." },
    { text: "Desenvolvemos uma logística de transporte otimizada para você..." },
    { text: "Programamos seu translado para quaisquer aeroportos do Brasil..." },
  ];

  return (
    <div className="aluguel-container">
      <h1>Frete de Ônibus</h1> {/* Título atualizado */}
      
      <div className="card-grid">
        {cards.map((card, index) => (
          <div className="aluguel-card" key={index}>
            <FontAwesomeIcon icon={faBus} className="aluguel-icon" />
            <p>{card.text}</p>
          </div>
        ))}
      </div>

      <div className="aluguel-buttons">
        <button className="button" onClick={() => navigate('/nossa-frota')}>
          Conhecer Frota
        </button>
        <button className="button" onClick={() => navigate('/contato')}>
          Fazer Orçamento
        </button>
      </div>

      {/* Formulário abaixo */}
      <div className="form-container">
        <h2>Preencha os dados e solicite um orçamento</h2>
        <form onSubmit={handleSubmit(onSubmit)}>
          <input
            type="text"
            name="nome"
            placeholder="Nome"
            value={values.nome}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="E-mail"
            value={values.email}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="telefone"
            placeholder="Telefone / WhatsApp"
            value={values.telefone}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="destino"
            placeholder="Destino"
            value={values.destino}
            onChange={handleChange}
            required
          />
          <div className="form-dates">
            <input
              type="date"
              name="dataIda"
              value={values.dataIda}
              onChange={handleChange}
              required
            />
            <input
              type="date"
              name="dataRetorno"
              value={values.dataRetorno}
              onChange={handleChange}
            />
          </div>
          <button type="submit" className="button">Fazer Orçamento</button>
        </form>
      </div>
    </div>
  );
};

export default AluguelOnibus;
