import React from 'react';
import { FaWhatsapp, FaInstagram, FaFacebook } from 'react-icons/fa';
import SimpleMap from '../components/SimpleMap';
import './Contato.css';

const Contato = () => {
  return (
    <div className="contact-page">
      <h2 style={{ textAlign: 'center', marginTop: '60px', fontSize: '24px' }}>
        Entre em contato conosco
      </h2>
      
      {/* Informações de contato */}
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
          <FaWhatsapp size={30} color="#25D366" style={{ marginRight: '10px' }} />
          <p style={{ fontSize: '18px', margin: '0' }}>(99) 99999-9999</p>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
          <FaInstagram size={30} color="#E1306C" style={{ marginRight: '10px' }} />
          <p style={{ fontSize: '18px', margin: '0' }}>@aloha_turismo</p>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '10px' }}>
          <FaFacebook size={30} color="#3b5998" style={{ marginRight: '10px' }} />
          <p style={{ fontSize: '18px', margin: '0' }}>/AlohaTurismo</p>
        </div>
        <p style={{ fontSize: '18px' }}>contato@alohaturismo.com</p>
      </div>

      {/* Adicionando o mapa */}
      <SimpleMap />

      {/* Links de redes sociais abaixo do mapa, se necessário */}
      {/* <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <a href="https://api.whatsapp.com/send?phone=5599999999999" target="_blank" rel="noopener noreferrer">
          <FaWhatsapp size={30} color="#25D366" style={{ margin: '0 10px' }} />
        </a>
        <a href="https://www.instagram.com/aloha_turismo/" target="_blank" rel="noopener noreferrer">
          <FaInstagram size={30} color="#E1306C" style={{ margin: '0 10px' }} />
        </a>
        <a href="https://www.facebook.com/AlohaTurismo" target="_blank" rel="noopener noreferrer">
          <FaFacebook size={30} color="#3b5998" style={{ margin: '0 10px' }} />
        </a>
      </div> */}
    </div>
  );
};

export default Contato;
