import React, { useEffect, useRef } from 'react';

const SimpleMap = () => {
  const mapRef = useRef(null);
  const coordinates = { lat: -25.359376907348633, lng: -51.476539611816406 }; // Suas coordenadas

  useEffect(() => {
    const { google } = window; // Acesse a biblioteca do Google Maps
    const map = new google.maps.Map(mapRef.current, {
      center: coordinates,
      zoom: 14,
      scrollwheel: false, // Desativa o scroll do mouse no mapa
    });

    new google.maps.Marker({
      position: coordinates,
      map: map,
      title: 'Nossa Localização', // Título do marcador
    });
  }, [coordinates]);

  return (
    <div>
      <h3 style={{ textAlign: 'center', fontSize: '20px', margin: '20px 0' }}>Nossa Localização</h3>
      <div
        ref={mapRef}
        style={{ width: '100%', height: '300px', marginTop: '20px' }} // Ajuste a altura do mapa
      ></div>
    </div>
  );
};

export default SimpleMap;
