import React, { useEffect } from 'react';

function Map() {
  useEffect(() => {
    // Inicializa o mapa após a carga do componente
    const map = new window.google.maps.Map(document.getElementById('map'), {
      center: { lat: -23.5505, lng: -46.6333 }, // Coordenadas de exemplo (São Paulo)
      zoom: 15,
    });

    // Adiciona um marcador no mapa para a localização da empresa
    new window.google.maps.Marker({
      position: { lat: -23.5505, lng: -46.6333 }, // Use as coordenadas reais da sua empresa
      map,
      title: 'Aloha Turismo',
    });
  }, []);

  return <div id="map" style={{ width: '100%', height: '400px', marginTop: '2rem' }}></div>;
}

export default Map;
