import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const SimpleMap = () => {
  useEffect(() => {
    const map = L.map('map').setView([-25.359376907348633, -51.476539611816406], 14); // Define a posição inicial do mapa

    // Adiciona o tile layer do OpenStreetMap
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 19,
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(map);

    // Adiciona um marcador
    L.marker([-25.359376907348633, -51.476539611816406]).addTo(map)
      .bindPopup('Estamos aqui')
      .openPopup();
  }, []);

  return <div id="map" style={{ width: '100%', height: '300px' }}></div>;
};

export default SimpleMap;
