import React, { useEffect } from 'react';

function SimpleMap() {
  useEffect(() => {
    // Função de inicialização do mapa
    const initMap = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        center: { lat: -25.359376907348633, lng: -51.476539611816406 },
        zoom: 14,
      });

      const marker = new window.google.maps.Marker({
        position: { lat: -25.359376907348633, lng: -51.476539611816406 },
        map: map,
        title: 'Minha Localização',
      });
    };

    // Carregar o Google Maps
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyAU_LI1KNjTOaRhkeFah0rJuton0r-xekk&callback=initMap`;
    script.async = true;
    script.onload = initMap; // Chamar initMap após o carregamento do script
    document.body.appendChild(script);
  }, []);

  return (
    <div>
      <h2>Localização da Empresa</h2>
      <div id="map" style={{ height: '400px', width: '100%' }}></div> {/* Defina a altura e largura do mapa */}
    </div>
  );
}

export default SimpleMap;
